css:
/* Reset and base styles */
* {
    box-sizing: border-box;
}

.print-format {
    margin: 0.2in;
    padding: 0;
    font-family: Arial, sans-serif;
    font-size: 9pt;
    line-height: 1.2;
}

/* Letter Head */
.letter-head {
    position: fixed;
}

/* Header section with logo and company info */
.invoice-header {
    border: 2px solid #000;
    margin-bottom: 2px;
}

.header-top {
    display: flex;
    align-items: flex-start;
    padding: 8px;
    border-bottom: 1px solid #000;
}

.company-logo {
    width: 100px;
    margin-right: 15px;
    flex-shrink: 0;
}

.company-info {
    flex: 1;
    text-align: center;
}

.company-name {
    font-size: 22pt;
    font-weight: bold;
    color: #00008B;
    letter-spacing: 1px;
    margin: 0;
}

.company-tagline {
    font-size: 8pt;
    color: #666;
    margin: 2px 0;
}

.company-address {
    font-size: 8pt;
    margin: 4px 0;
}

.company-contact {
    display: flex;
    justify-content: flex-end;
    flex-direction: column;
    text-align: right;
    font-size: 8pt;
    min-width: 120px;
}

/* Authorized dealer section */
.dealer-section {
    background: #fff8dc;
    padding: 6px;
    text-align: center;
    border-bottom: 1px solid #000;
}

.dealer-title {
    font-size: 8pt;
    font-weight: bold;
    margin-bottom: 4px;
}

.dealer-logos {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    gap: 8px;
}

.dealer-logos img {
    height: 25px;
    max-width: 80px;
    object-fit: contain;
}

/* Title section */
.invoice-title {
    color: black;
    text-align: center;
    padding: 6px;
    font-size: 14pt;
    font-weight: bold;
    border: 1px solid #000;
}

/* Details section */
.details-grid {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    border-bottom: 2px solid #000;
}

.detail-box {
    border-right: 1px solid #000;
    border-left: 1px solid #000;
    padding: 8px;
    font-size: 8pt;
}

.detail-box:last-child {
    padding: 0;
}

.detail-title {
    font-weight: bold;
    font-size: 9pt;
    margin-bottom: 6px;
}

.detail-row {
    display: flex;
    margin-bottom: 3px;
}

.detail-label {
    min-width: 90px;
    font-weight: normal;
}

.detail-value {
    flex: 1;
}

/* Invoice details table */
.invoice-details-table {
    width: 100%;
    border-collapse: collapse;
}

.invoice-details-table td {
    padding: 3px 0;
    font-size: 8pt;
}

.invoice-details-table tr {
    border-bottom: 1px solid #000;
}

.invoice-details-table tr:last-child {
    border-bottom: none;
}

.invoice-label {
    font-weight: normal;
    width: 110px;
    border-right: 1px solid #000;
}

.invoice-value {
    font-weight: normal;
}

/* Items table */

.items-table {
    width: 100%;
    border-collapse: collapse;
}

.items-table th,
.items-table td {
    border: 1px solid #000;
    padding: 4px;
    font-size: 8pt;
    text-align: left;
}

.items-table th {
    background: #f0f0f0;
    font-weight: bold;
    text-align: center;
}

.items-table td {
    vertical-align: top;
}

.text-center {
    text-align: center !important;
}

.text-right {
    text-align: right !important;
}

/* Transport and totals section */
.bottom-section {
    display: flex;
    border-bottom: 2px solid #000;
}

.transport-details {
    flex: 1;
    padding: 8px;
    border-right: 1px solid #000;
    border-left: 1px solid #000;
    font-size: 8pt;
}

.totals-section {
    width: 200px;
    border-right: 1px solid #000;
}

.totals-table {
    width: 100%;
    border-collapse: collapse;
}

.totals-table td {
    border: 1px solid #000;
    padding: 4px 8px;
    font-size: 8pt;
}

.total-row {
    font-weight: bold;
    background: #f0f0f0;
}

/* Amount in words */
.amount-words {
    padding: 6px 8px;
    font-size: 8pt;
    border-bottom: 1px solid #000;
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

/* Terms and bank details */
.footer-section {
    display: flex;
    border: 1px solid #000;
}

.bank-details {
    flex: 1;
    padding: 8px;
    font-size: 8pt;
    border-right: 1px solid #000;
}

.signature-section {
    width: 220px;
    padding: 8px;
    text-align: center;
}

.signature-box {
    margin-top: 40px;
    padding-top: 8px;
    border-top: 1px solid #000;
}

.company-footer {
    font-weight: bold;
    font-size: 9pt;
    margin-bottom: 20px;
}

/* Small text */
.small-text {
    border-right: 1px solid #000;
    border-left: 1px solid #000;
    font-size: 7pt;
    line-height: 1.3;
}

.bold {
    font-weight: bold;
}

/* Page break handling */
@media print {
    .print-format {
        height: 100vh;
    }

    .items-table {
        page-break-inside: auto;
    }

    .items-table tr {
        page-break-inside: avoid;
        page-break-after: auto;
    }
}

/* Reset and base styles */
* {
    box-sizing: border-box;
}

.print-format {
    margin: 0.2in;
    padding: 0;
    font-family: Arial, sans-serif;
    font-size: 10pt;
    line-height: 1.3;
}

/* Page border */
.stock-transfer-container {
    border: 2px solid #000;
    padding: 0;
}

/* Stock Transfer Title */
.stock-transfer-title {
    text-align: center;
    padding: 8px;
    font-size: 16pt;
    font-weight: bold;
    background: #f0f0f0;
    border-bottom: 2px solid #000;
}

/* Branch and info section - all side by side */
.branch-info-section {
    display: grid;
    grid-template-columns: 30% 30% 40%;
    border-bottom: 2px solid #000;
}

.branch-box {
    padding: 10px;
    border-right: 1px solid #000;
}

.branch-box:last-child {
    border-right: none;
}

.branch-label {
    font-weight: bold;
    font-size: 11pt;
    margin-bottom: 8px;
}

.branch-value {
    font-size: 10pt;
    min-height: 40px;
}

/* Date and Stock Transfer No in one box */
.info-box {
    padding: 10px;
}

.info-row {
    display: flex;
    margin-bottom: 8px;
}

.info-row:last-child {
    margin-bottom: 0;
}

.info-label {
    font-weight: bold;
    margin-right: 10px;
    min-width: 130px;
}

.info-value {
    flex: 1;
}

/* Items table */
